import "./App.css";
import React, { Component } from "react";
import env from "react-dotenv";

class App extends Component {
  state = {
    data: [],
    getData: false,
    searchWord: "",
  };

  componentDidMount = async () => {
    const key = env.API_KEY;
    const urlSearch =
      "/api/charities/v2/search?name=bike&fields=id,name,address,abstract&results=20&page=2";
    const myHeaders = new Headers();
    myHeaders.append("X-API-Key", key);

    const requestOption = {
      method: "GET",
      headers: myHeaders,
    };

    const response = await fetch(urlSearch, requestOption);
    const data = await response.json();
    this.setState({
      data: data.charities,
      getData: true,
    });
  };

  handleWord = (e) => {
    const searchWord = e.target.value;
    this.setState({
      searchWord,
    });
  };

  handleSearch = () => {};

  render() {
    const { data, getData } = this.state;
    return (
      <div>
        <input id="search" onChange={(e) => this.handleWord(e)}></input>
        <button id="searchBtn" onClick={this.handleSearch}>
          Search
        </button>
        {/* {getData && data.map((item) => <div key={item.id}>{item.name}</div>)} */}
      </div>
    );
  }
}

export default App;
