import "./App.css";
import React, { Component } from "react";
import env from "react-dotenv";

class App extends Component {
  state = {
    data: [],
    getData: false,
    searchWord: "",
    searchedData: [],
  };

  componentDidMount = async () => {
    const key = env.API_KEY;
    const urlSearch =
      "/api/charities/v2/search?name=bike&fields=id,name,address,abstract&results=20&page=2";
    const myHeaders = new Headers();
    myHeaders.append("X-API-Key", "47TE87C-YYA4567-MSPD4RJ-NCRBF65");

    const requestOption = {
      method: "GET",
      headers: myHeaders,
    };

    const response = await fetch(urlSearch, requestOption);
    const data = await response.json();
    this.setState({
      data: data.charities,
      getData: true,
    });
  };

  handleWord = (e) => {
    const searchWord = e.target.value;
    this.setState({
      searchWord,
    });
  };

  handleSearch = () => {
    const searchWord = this.state.searchWord;
    console.log("data ", this.state.data);
    const searchedData = this.state.data.filter((word) =>
      word.name.includes(searchWord)
    );
    console.log("search", searchedData);
    this.setState({
      searchedData,
    });
  };

  render() {
    const { searchedData, getData } = this.state;
    return (
      <div>
        <input id="search" onChange={(e) => this.handleWord(e)}></input>
        <button id="searchBtn" onClick={this.handleSearch}>
          Search
        </button>
        {getData &&
          searchedData.map((item) => <div key={item.id}>{item.name}</div>)}
      </div>
    );
  }
}

export default App;
